// Utility to fetch questions from questions.json
import axios from 'axios';

export async function fetchQuestions() {
  // For local dev, fetch from public folder or backend endpoint
  // Here, assuming questions.json is served from public folder
  const response = await axios.get('/questions.json');
  return response.data;
}
