import React, { useState, useEffect, useRef } from 'react';
import { fetchQuestions } from '../../utils/questions';
import { Play, Download, Copy, Sun, Moon, Code2, XCircle, Settings, Maximize2, Terminal, FileText } from 'lucide-react';
import './Editor.css';

const LANGUAGES = [
  { value: 'javascript', label: 'JavaScript', icon: <Code2 size={18} /> },
  { value: 'python', label: 'Python', icon: <Code2 size={18} /> },
  { value: 'java', label: 'Java', icon: <Code2 size={18} /> },
  { value: 'cpp', label: 'C++', icon: <Code2 size={18} /> },
  { value: 'c', label: 'C', icon: <Code2 size={18} /> },
];

const THEMES = [
  { value: 'dark', label: 'Dark', icon: <Moon size={18} /> },
  { value: 'light', label: 'Light', icon: <Sun size={18} /> },
];

const Editor = () => {
  const [code, setCode] = useState('');
  const [language, setLanguage] = useState('javascript');
  const [theme, setTheme] = useState('dark');
  const [fontSize, setFontSize] = useState(16);
  const [output, setOutput] = useState('');
  const [isCopied, setIsCopied] = useState(false);
  const [input, setInput] = useState('');
  const [questions, setQuestions] = useState([]);
  const [selectedQid, setSelectedQid] = useState('');
  const [selectedQuestion, setSelectedQuestion] = useState(null);
  const codeRef = useRef(null);
  const lineRef = useRef(null);

  // Load questions and set from query param if present
  useEffect(() => {
    fetchQuestions().then(qs => {
      setQuestions(qs);
      const params = new URLSearchParams(window.location.search);
      const qid = params.get('qid');
      if (qid) {
        setSelectedQid(qid);
        const q = qs.find(q => q.id === qid);
        setSelectedQuestion(q || null);
      }
    });
  }, []);

  useEffect(() => {
    if (selectedQid && questions.length) {
      const q = questions.find(q => q.id === selectedQid);
      setSelectedQuestion(q || null);
    }
  }, [selectedQid, questions]);

  // line numbers
  const getLineNumbers = () => {
    return code.split('\n').map((_, i) => i + 1).join('\n');
  };

  // scroll sync
  const handleScroll = () => {
    if (lineRef.current && codeRef.current) {
      lineRef.current.scrollTop = codeRef.current.scrollTop;
    }
  };

  // copy code
  const handleCopy = () => {
    navigator.clipboard.writeText(code);
    setIsCopied(true);
  };

  // download code
  const handleDownload = () => {
    const ext = language === 'python' ? 'py' : language === 'java' ? 'java' : language === 'cpp' ? 'cpp' : language === 'c' ? 'c' : 'js';
    const blob = new Blob([code], { type: 'text/plain' });
    const url = URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = `solution.${ext}`;
    document.body.appendChild(a);
    a.click();
    document.body.removeChild(a);
    URL.revokeObjectURL(url);
  };

  // run code
  const onRunCode = async () => {
    try {
      const token = localStorage.getItem('token');
      const userId = localStorage.getItem('userId');

      const response = await fetch('http://localhost:5000/api/code/execute', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          ...(token && { Authorization: `Bearer ${token}` }),
        },
        body: JSON.stringify({
          language,
          code,
          input,
          userId,
          questionId: selectedQid,
        }),
      });

      const data = await response.json();
      setOutput(data.output || data.error || 'No output');
    } catch (error) {
      setOutput('Error connecting to server');
      console.error(error);
    }
  };

  // clear output
  const onClearOutput = () => {
    setOutput('');
  };

  useEffect(() => {
    if (isCopied) {
      const t = setTimeout(() => setIsCopied(false), 1200);
      return () => clearTimeout(t);
    }
  }, [isCopied]);

  return (
    <div className="editor-container">
      {/* Header Section */}
      <div className="editor-header">
        <div className="header-left">
          <div className="editor-title">
            <Code2 size={28} />
            <h1>Code Editor</h1>
          </div>
          <div className="editor-subtitle">
            Write, test, and perfect your solutions
          </div>
        </div>
        <div className="header-actions">
          <button className="header-btn" title="Settings">
            <Settings size={20} />
          </button>
          <button className="header-btn" title="Fullscreen">
            <Maximize2 size={20} />
          </button>
        </div>
      </div>

      {/* Toolbar Section */}
      <div className="editor-toolbar">
        <div className="toolbar-section">
          <div className="toolbar-group">
            <label className="toolbar-label">Language</label>
            <div className="select-wrapper">
              <select className="editor-select" value={language} onChange={e => setLanguage(e.target.value)}>
                {LANGUAGES.map(l => (
                  <option key={l.value} value={l.value}>{l.label}</option>
                ))}
              </select>
            </div>
          </div>
          
          <div className="toolbar-group">
            <label className="toolbar-label">Theme</label>
            <div className="select-wrapper">
              <select className="editor-select" value={theme} onChange={e => setTheme(e.target.value)}>
                {THEMES.map(t => (
                  <option key={t.value} value={t.value}>{t.label}</option>
                ))}
              </select>
            </div>
          </div>
          
          <div className="toolbar-group">
            <label className="toolbar-label">Font Size</label>
            <div className="font-size-control">
              <input 
                type="range" 
                min={12} 
                max={24} 
                value={fontSize} 
                onChange={e => setFontSize(Number(e.target.value))} 
                className="font-slider" 
              />
              <span className="font-size-label">{fontSize}px</span>
            </div>
          </div>
        </div>

        <div className="toolbar-section">
          <div className="toolbar-group">
            <label className="toolbar-label">Question</label>
            <div className="select-wrapper">
              <select
                className="editor-select question-select"
                value={selectedQid}
                onChange={e => setSelectedQid(e.target.value)}
              >
                <option value="">Choose a Question</option>
                {questions.map(q => (
                  <option key={q._id} value={q._id}>
                    {q.id} - {q.question.slice(0, 40)}...
                  </option>
                ))}
              </select>
            </div>
          </div>
          
          <div className="toolbar-actions">
            <button className="action-btn secondary-btn" onClick={handleCopy} title="Copy Code">
              <Copy size={18} />
              <span>Copy</span>
              {isCopied && <div className="copied-indicator">Copied!</div>}
            </button>
            <button className="action-btn secondary-btn" onClick={handleDownload} title="Download Code">
              <Download size={18} />
              <span>Download</span>
            </button>
            <button className="action-btn primary-btn" onClick={onRunCode} title="Run Code">
              <Play size={18} />
              <span>Run Code</span>
            </button>
          </div>
        </div>
      </div>

      {/* Question Display */}
      {selectedQuestion && (
        <div className="question-panel">
          <div className="question-header">
            <FileText size={20} />
            <h3>Question {selectedQuestion.id}</h3>
          </div>
          <div className="question-content">
            <p className="question-text">{selectedQuestion.question}</p>
            <div className="question-meta">
              <div className="meta-item">
                <strong>Input:</strong> {selectedQuestion.input_format}
              </div>
              <div className="meta-item">
                <strong>Output:</strong> {selectedQuestion.output_format}
              </div>
            </div>
          </div>
        </div>
      )}

      {/* Main Editor Area */}
      <div className="editor-workspace">
        {/* Code Editor Panel */}
        <div className="code-panel">
          <div className="panel-header">
            <div className="panel-title">
              <Code2 size={18} />
              <span>Code Editor</span>
            </div>
          </div>
          <div className="code-editor-container">
            <div className="line-numbers" ref={lineRef} style={{ fontSize: fontSize }}>
              {getLineNumbers()}
            </div>
            <textarea
              className="code-editor"
              ref={codeRef}
              value={code}
              onChange={e => setCode(e.target.value)}
              onScroll={handleScroll}
              style={{ fontSize: fontSize }}
              spellCheck={false}
              placeholder="// Welcome to the Code Editor
// Start writing your solution here...

function solve() {
    // Your code here
}"
            />
          </div>
        </div>

        {/* Output Panel */}
        <div className="output-panel">
          {/* Input Section */}
          <div className="input-section">
            <div className="panel-header">
              <div className="panel-title">
                <Terminal size={18} />
                <span>Input (stdin)</span>
              </div>
            </div>
            <textarea
              className="input-area"
              value={input}
              onChange={e => setInput(e.target.value)}
              placeholder="Enter input for your program here...
Example: 
5
1 2 3 4 5"
            />
          </div>

          {/* Output Section */}
          <div className="output-section">
            <div className="panel-header">
              <div className="panel-title">
                <Terminal size={18} />
                <span>Output</span>
              </div>
              <button className="clear-output-btn" onClick={onClearOutput} title="Clear Output">
                <XCircle size={16} />
              </button>
            </div>
            
            <div className="output-content">
              {output ? (
                <pre className="output-text">{output}</pre>
              ) : (
                <div className="output-placeholder">
                  <div className="placeholder-icon">
                    <Play size={32} />
                  </div>
                  <div className="placeholder-text">
                    <h4>Ready to Run</h4>
                    <p>Click "Run Code" to execute your program and see the output here.</p>
                  </div>
                </div>
              )}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Editor;
