import React, { useState, useEffect } from 'react';
import { Code, Star } from 'lucide-react';
import './dashboard.css';
import { fetchQuestions } from '../../utils/questions';

const Dashboard = () => {
  const [selectedQuestion, setSelectedQuestion] = useState(null);
  const [questions, setQuestions] = useState([]);
  const [bookmarkedQuestions, setBookmarkedQuestions] = useState(new Set());
  const [filterTag, setFilterTag] = useState('');
  const [search, setSearch] = useState('');

  useEffect(() => {
    fetchQuestions().then(setQuestions);
  }, []);

  const toggleBookmark = (qid) => {
    const newBookmarks = new Set(bookmarkedQuestions);
    newBookmarks.has(qid) ? newBookmarks.delete(qid) : newBookmarks.add(qid);
    setBookmarkedQuestions(newBookmarks);
  };

  const allTags = Array.from(
    new Set(questions.flatMap(q => q.tags.concat(q.difficulty ? [q.difficulty] : [])))
  );

  const filteredQuestions = questions.filter(q =>
    (!filterTag || q.tags.includes(filterTag) || q.difficulty === filterTag) &&
    (!search || q.question.toLowerCase().includes(search.toLowerCase()))
  );

  const totalQuestions = questions.length;
  const solvedCount = 0;
  const bookmarkedCount = bookmarkedQuestions.size;
  const acceptanceRate = '‚Äî';

  const QuestionCard = ({ question }) => (
    <div
      className={`problem-card ${selectedQuestion?.id === question.id ? 'border-teal-400' : ''}`}
      onClick={() => setSelectedQuestion(question)}
    >
      <div className="problem-card-header">
        <h3>Q: {question.id}</h3>
        <button onClick={e => { e.stopPropagation(); toggleBookmark(question.id); }}>
          <Star
            size={16}
            fill={bookmarkedQuestions.has(question.id) ? '#ecc94b' : 'none'}
            color={bookmarkedQuestions.has(question.id) ? '#ecc94b' : '#b2f5ea'}
          />
        </button>
      </div>
      <p className="problem-card-title">{question.question}</p>
      <div className="tags">
        {question.tags.map(tag => <span key={tag} className="tag">{tag}</span>)}
        {question.difficulty && (
          <span className={`tag difficulty-tag difficulty-${question.difficulty.toLowerCase()}`}>
            {question.difficulty}
          </span>
        )}
      </div>
    </div>
  );

  return (
    <div className="dashboard-container">
      {/* HEADER */}
      <div className="dashboard-header">
        <div className="dashboard-title-block">
          <h1 className="dashboard-title">Dashboard ¬∑ ZCODER</h1>
          <p className="dashboard-subtitle">Welcome back, coder! Keep your streak strong and your logic sharper.</p>
        </div>
        <div className="dashboard-stats">
          <div className="stat-card">
            <div className="stat-number">{solvedCount}</div>
            <div className="stat-label">Solved</div>
          </div>
          <div className="stat-card">
            <div className="stat-number">{totalQuestions}</div>
            <div className="stat-label">Total</div>
          </div>
          <div className="stat-card">
            <div className="stat-number">{bookmarkedCount}</div>
            <div className="stat-label">Bookmarked</div>
          </div>
          <div className="stat-card">
            <div className="stat-number">{acceptanceRate}</div>
            <div className="stat-label">Acceptance</div>
          </div>
        </div>
      </div>

      {/* INTRO & TIPS */}
      <div className="dashboard-intro">
        <p>
          Your journey to becoming a code ninja starts here. Dive into handpicked problems, flex your brain, and build that muscle memory for patterns. This isn‚Äôt just another platform‚Äîit‚Äôs your personal battleground of bugs and brilliance.
        </p>
      </div>

      <div className="dashboard-tips">
        <h4>Pro Tips üß†</h4>
        <ul>
          <li>üî• <strong>Filter wisely:</strong> Master one tag at a time. Today trees, tomorrow DP.</li>
          <li>üìö <strong>Bookmark the beasts:</strong> Found a tough nut? Mark it, crack it later.</li>
          <li>üõ†Ô∏è <strong>Build patterns:</strong> Repetition isn‚Äôt boring‚Äîit‚Äôs brain gains.</li>
          <li>üß™ <strong>Debug like a scientist:</strong> Hypothesize, test, iterate. Your console is your lab.</li>
          <li>‚ö° <strong>Speed comes later:</strong> First get it right. Then get it fast.</li>
        </ul>
      </div>

      {/* MAIN PANEL */}
      <div className="dashboard-main">
        <div className="dashboard-left">
          <h3>Questions</h3>
          <div className="filters">
            <input
              type="text"
              placeholder="Search..."
              value={search}
              onChange={e => setSearch(e.target.value)}
            />
            <select value={filterTag} onChange={e => setFilterTag(e.target.value)}>
              <option value="">All Tags</option>
              {allTags.map(tag => <option key={tag} value={tag}>{tag}</option>)}
            </select>
          </div>
          <div className="question-list">
            {filteredQuestions.map(q => <QuestionCard key={q.id} question={q} />)}
            {filteredQuestions.length === 0 && (
              <div className="empty-msg">No questions found.</div>
            )}
          </div>
        </div>

        <div className="dashboard-right">
          {selectedQuestion ? (
            <div className="question-detail">
              <h3>Q: {selectedQuestion.id}</h3>
              <p>{selectedQuestion.question}</p>
              <p>
                <strong>Tags:</strong> {selectedQuestion.tags.join(', ')}{' '}
                {selectedQuestion.difficulty && (
                  <span className={`tag difficulty-tag difficulty-${selectedQuestion.difficulty.toLowerCase()}`}>
                    {selectedQuestion.difficulty}
                  </span>
                )}
              </p>
              <p><strong>Constraints:</strong> {selectedQuestion.constraints}</p>
              <p><strong>Input Format:</strong> {selectedQuestion.input_format}</p>
              <p><strong>Output Format:</strong> {selectedQuestion.output_format}</p>
              <div>
                <strong>Sample Input:</strong>
                <pre>{selectedQuestion.demo_input}</pre>
              </div>
              <div>
                <strong>Sample Output:</strong>
                <pre>{selectedQuestion.demo_output}</pre>
              </div>
              <button
                onClick={() => window.location.href = `/editor?qid=${selectedQuestion.id}`}
                className="primary-button"
              >
                Go to Editor & Solve
              </button>
            </div>
          ) : (
            <div className="no-question-selected">
              <Code size={48} />
              <h3>Select a Question</h3>
              <p>Choose a question from the left panel to view details and start solving.</p>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
