import React, { useState, useEffect, useRef } from 'react';
import { fetchQuestions } from '../../utils/questions';
import { Play, Download, Copy, Sun, Moon, Code2, XCircle, User, LogOut } from 'lucide-react';
import './Editor.css';
import axios from "axios";
import { CheckCircle } from "react-feather";
import SplitPane from 'react-split-pane';

const LANGUAGES = [
  { value: 'javascript', label: 'JavaScript', icon: <Code2 size={16} /> },
  { value: 'python', label: 'Python', icon: <Code2 size={16} /> },
  { value: 'java', label: 'Java', icon: <Code2 size={16} /> },
  { value: 'cpp', label: 'C++', icon: <Code2 size={16} /> },
  { value: 'c', label: 'C', icon: <Code2 size={16} /> },
];

const THEMES = [
  { value: 'dark', label: 'Dark', icon: <Moon size={16} /> },
  { value: 'light', label: 'Light', icon: <Sun size={16} /> },
];
const Editor = () => {
  const [code, setCode] = useState('');
  const [language, setLanguage] = useState('javascript');
  const [theme, setTheme] = useState('dark');
  const [fontSize, setFontSize] = useState(16);
  const [input, setInput] = useState('');
  const [isCopied, setIsCopied] = useState(false);
  const [questions, setQuestions] = useState([]);
  const [selectedQid, setSelectedQid] = useState('');
  const [selectedQuestion, setSelectedQuestion] = useState(null);
  const [username, setUsername] = useState('');
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  const codeRef = useRef(null);
  const lineRef = useRef(null);

  useEffect(() => {
    fetchQuestions().then(qs => {
      setQuestions(qs);
      const qid = new URLSearchParams(window.location.search).get('qid');
      if (qid) {
        setSelectedQid(qid);
        setSelectedQuestion(qs.find(q => q._id === qid) || null);
      }
    });
    const token = localStorage.getItem('token');
    const user = localStorage.getItem('username');
    if (token && user) {
      setIsLoggedIn(true);
      setUsername(user);
    }
  }, []);

  useEffect(() => {
    if (selectedQid && questions.length)
      setSelectedQuestion(questions.find(q => q._id === selectedQid) || null);
  }, [selectedQid, questions]);

  const getLineNumbers = () => code.split('\n').map((_, i) => i + 1).join('\n');

  const handleScroll = () => {
    if (lineRef.current && codeRef.current)
      lineRef.current.scrollTop = codeRef.current.scrollTop;
  };

  const handleCopy = () => {
    navigator.clipboard.writeText(code);
    setIsCopied(true);
  };

  const handleDownload = () => {
    const ext = { python: 'py', java: 'java', cpp: 'cpp', c: 'c', javascript: 'js' }[language];
    const blob = new Blob([code], { type: 'text/plain' });
    const a = document.createElement('a');
    a.href = URL.createObjectURL(blob);
    a.download = `solution.${ext}`;
    a.click();
    URL.revokeObjectURL(a.href);
  };

  const onRunCode = async () => {
    try {
      const res = await fetch('http://localhost:5000/api/code/execute', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          Authorization: `Bearer ${localStorage.getItem('token')}`,
        },
        body: JSON.stringify({
          language,
          code,
          input,
          userId: localStorage.getItem('userId'),
          questionId: selectedQid,
        }),
      });
      const data = await res.json();
      alert(data.output || data.error || 'No output returned.');
    } catch {
      alert("Error executing code.");
    }
  };

  const handleSubmit = async () => {
    try {
      const res = await axios.post("http://localhost:5000/api/code/submit", {
        code,
        language,
        questionId: selectedQid,
        userId: localStorage.getItem('userId'),
      });
      alert(res.data.passed ? "✅ All test cases passed!" : "❌ Some test cases failed.");
    } catch (err) {
      console.error("Submit error", err);
      alert("Submission failed.");
    }
  };

  const handleSignOut = () => {
    localStorage.clear();
    setIsLoggedIn(false);
    setUsername('');
    window.location.href = '/login';
  };

  const getUserInitials = (name) =>
    name.split(' ').map(w => w[0]).join('').toUpperCase().slice(0, 2);

  useEffect(() => {
    if (isCopied) setTimeout(() => setIsCopied(false), 1200);
  }, [isCopied]);

  return (
    <div className={`editor-glass editor-theme-${theme}`}>
      <div className="editor-glow" />
      <div className="editor-toolbar">
        <div className="toolbar-group">
          <select className="editor-select" value={language} onChange={e => setLanguage(e.target.value)}>
            {LANGUAGES.map(l => <option key={l.value} value={l.value}>{l.label}</option>)}
          </select>
          <select className="editor-select" value={theme} onChange={e => setTheme(e.target.value)}>
            {THEMES.map(t => <option key={t.value} value={t.value}>{t.label}</option>)}
          </select>
          <select className="editor-select" value={selectedQid} onChange={e => setSelectedQid(e.target.value)}>
            <option value="">Choose Question</option>
            {questions.map(q => (
              <option key={q._id} value={q._id}>{q.id} - {q.question.slice(0, 30)}...</option>
            ))}
          </select>
        </div>
        <div className="toolbar-group">
          <input type="range" min={12} max={22} value={fontSize} onChange={e => setFontSize(+e.target.value)} className="font-slider" />
          <span className="font-size-label">{fontSize}px</span>
        </div>
        <div className="toolbar-group">
          <button className="icon-btn" onClick={handleCopy}><Copy size={18} />{isCopied && <span className="copied-badge">Copied!</span>}</button>
          <button className="icon-btn" onClick={handleDownload}><Download size={18} /></button>
          <button className="icon-btn run-btn" onClick={onRunCode}><Play size={18} /></button>
          <button className="icon-btn submit-btn" onClick={handleSubmit}><CheckCircle size={18} /></button>
        </div>
        {isLoggedIn && (
          <div className="toolbar-group user-profile-section">
            <div className="user-profile">
              <div className="user-avatar">{getUserInitials(username)}</div>
              <span className="username-text">{username}</span>
            </div>
            <button className="icon-btn signout-btn" onClick={handleSignOut}><LogOut size={16} /></button>
          </div>
        )}
      </div>

      <div className="leetcode-editor-layout">
        <div className="question-panel">
          {selectedQuestion ? (
            <>
              <h2>{selectedQuestion.id}. {selectedQuestion.question}</h2>
              <h3>Input Format</h3>
              <p>{selectedQuestion.input_format}</p>
              <h3>Output Format</h3>
              <p>{selectedQuestion.output_format}</p>
              <h3>Sample Input</h3>
              <pre>{selectedQuestion.sample_input}</pre>
              <h3>Sample Output</h3>
              <pre>{selectedQuestion.sample_output}</pre>
            </>
          ) : (
            <p style={{ color: '#aaa' }}>Please select a question to view description.</p>
          )}
        </div>

        <div className="code-panel">
          <div className="editor-main">
            <div className="editor-code-area">
              <textarea className="editor-lines" ref={lineRef} value={getLineNumbers()} readOnly
                style={{ fontSize, color: theme === 'light' ? '#38b2ac' : '#63b3ed' }} />
              <textarea className="editor-code" ref={codeRef} value={code}
                onChange={e => setCode(e.target.value)} onScroll={handleScroll}
                style={{ fontSize }} spellCheck={false} placeholder="// Start coding..." />
            </div>
            <div style={{ marginTop: 12 }}>
              <label className="output-title">Input (stdin)</label>
              <textarea className="editor-input"
                value={input} onChange={e => setInput(e.target.value)}
                placeholder="Enter sample input" style={{ fontSize: 15, minHeight: 60, width: '100%' }} />
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Editor;
