import React, { useState, useEffect } from 'react';
import { Code } from 'lucide-react';
import './dashboard.css';
import { fetchQuestions } from '../../utils/questions';


const Dashboard = () => {
  const [selectedQuestion, setSelectedQuestion] = useState(null);
  const [questions, setQuestions] = useState([]);
  const [bookmarkedQuestions, setBookmarkedQuestions] = useState(new Set());
  const [filterTag, setFilterTag] = useState('');
  const [search, setSearch] = useState('');
  const [solvedCount, setSolvedCount] = useState(null);
  const [submissionCount, setSubmissionCount] = useState(null);
  const fetchBookmarks = async (uid) => {
  try {
    const res = await fetch(`http://localhost:5000/api/bookmarks/${uid}`);
    const data = await res.json();
    setBookmarkedQuestions(new Set(data.bookmarks.map(q => q._id)));
  } catch (err) {
    console.error('Failed to fetch bookmarks:', err);
  }
};

  const fetchDashboardMetrics = async (userId) => {
  const res = await fetch(`http://localhost:5000/api/code/metrics/${userId}`); // Updated to absolute URL for dev
  if (!res.ok) throw new Error('Failed to fetch dashboard metrics');
  return res.json();
};
  useEffect(() => {
  const uid = localStorage.getItem('userId');
  if (!uid) return;

  const fetchAllData = async () => {
    try {
      const metrics = await fetchDashboardMetrics(uid);
      console.log("‚úÖ Metrics received:", metrics);
      setSolvedCount(metrics.solved);
      setSubmissionCount(metrics.submissions);

      // ‚úÖ accurate bookmarks (from /api/bookmarks)
      const res = await fetch(`http://localhost:5000/api/bookmarks/${uid}`);
      const data = await res.json();
      setBookmarkedQuestions(new Set(data.bookmarks.map(q => q._id))); // use _id for Mongo refs
    } catch (err) {
      console.error("‚ùå Error fetching dashboard data:", err);
    }
  };

  fetchAllData();
  fetchQuestions().then(setQuestions);
}, []);



  const toggleBookmark = async (qid) => {
  const uid = localStorage.getItem('userId');
  if (!uid) return;

  try {
    const res = await fetch('http://localhost:5000/api/bookmarks/toggle', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ userId: uid, questionId: qid }),
    });

    const data = await res.json();
    if (data.success) {
      // fetchBookmarks again to sync state from DB
      await fetchBookmarks(uid);
    }
  } catch (err) {
    console.error('Bookmark toggle failed:', err);
  }
};



  const allTags = Array.from(
    new Set(questions.flatMap(q => q.tags.concat(q.difficulty ? [q.difficulty] : [])))
  );

  const filteredQuestions = questions.filter(q =>
    (!filterTag || q.tags.includes(filterTag) || q.difficulty === filterTag) &&
    (!search || q.question.toLowerCase().includes(search.toLowerCase()))
  );

  const QuestionCard = ({ question }) => (
    <div
      className={`problem-card ${selectedQuestion?.id === question.id ? 'border-teal-400' : ''}`}
      onClick={() => setSelectedQuestion(question)}
    >
      <div className="problem-card-header">
        <h3>Q: {question.id}</h3>
        <span
          onClick={e => { e.stopPropagation(); toggleBookmark(question._id); }}
          style={{ cursor: 'pointer', fontSize: 22, color: bookmarkedQuestions.has(question._id) ? '#ecc94b' : '#b2f5ea' }}
        >
          {bookmarkedQuestions.has(question._id) ? '‚òÖ' : '‚òÜ'}
        </span>
      </div>
      <p className="problem-card-title">{question.question}</p>
      <div className="tags">
        {question.tags.map(tag => <span key={tag} className="tag">{tag}</span>)}
        {question.difficulty && (
          <span className={`tag difficulty-tag difficulty-${question.difficulty.toLowerCase()}`}>
            {question.difficulty}
          </span>
        )}
      </div>
    </div>
  );

  return (
    <div className="dashboard-container">
      {/* Header Section */}
      <header className="dashboard-header">
        <div className="header-content">
          <div className="title-section">
            <h1 className="dashboard-title">Dashboard ¬∑ ZCODER</h1>
            <p className="welcome-message">
              Welcome back, coder! Keep your streak strong and your logic sharper.
            </p>
          </div>
          
          {/* Stats Section */}
          <div className="stats-grid">
            <div className="stat-card">
              <div className="stat-number">{questions.length}</div>
              <div className="stat-label">Total Questions</div>
            </div>
            <div className="stat-card">
              <div className="stat-number">{solvedCount > 0 ? solvedCount : '--'}</div>
              <div className="stat-label">Solved</div>
            </div>
            <div className="stat-card">
              <div className="stat-number">{submissionCount > 0 ? submissionCount : '--'}</div>
              <div className="stat-label">Submissions</div>
            </div>
            <div className="stat-card">
              <div className="stat-number">{bookmarkedQuestions.size > 0 ? bookmarkedQuestions.size : '--'}</div>
              <div className="stat-label">Bookmarked</div>
            </div>
          </div>
        </div>
        
        <div className="description-section">
          <p className="description-text">
            Your journey to becoming a code ninja starts here. Dive into handpicked problems, flex your brain, and build that muscle memory for patterns. This isn't just another platform‚Äîit's your personal battleground of bugs and brilliance.
          </p>
        </div>
      </header>

      {/* Pro Tips Section */}
      <section className="tips-section">
        <h3 className="tips-title">Pro Tips üß†</h3>
        <ul className="tips-list">
          <li><strong>üî• Filter wisely:</strong> Master one tag at a time. Today trees, tomorrow DP.</li>
          <li><strong>üìö Bookmark the beasts:</strong> Found a tough nut? Mark it, crack it later.</li>
          <li><strong>üõ†Ô∏è Build patterns:</strong> Repetition isn't boring‚Äîit's brain gains.</li>
          <li><strong>üß™ Debug like a scientist:</strong> Hypothesize, test, iterate. Your console is your lab.</li>
          <li><strong>‚ö° Speed comes later:</strong> First get it right. Then get it fast.</li>
        </ul>
      </section>

      {/* Main Content Section */}
      <main className="main-content">
        {/* Questions Panel */}
        <aside className="questions-panel">
          <div className="panel-header">
            <h3 className="panel-title">Questions</h3>
            <div className="search-controls">
              <input
                type="text"
                placeholder="üîç Search by title or keyword..."
                value={search}
                onChange={e => setSearch(e.target.value)}
                className="search-input"
              />
              <select
                value={filterTag}
                onChange={e => setFilterTag(e.target.value)}
                className="filter-select"
              >
                <option value="">All Tags</option>
                {allTags.map(tag => (<option key={tag} value={tag}>{tag}</option>))}
              </select>
            </div>
          </div>
          
          <div className="questions-list">
            {filteredQuestions.map(q => (<QuestionCard key={q.id} question={q} />))}
            {filteredQuestions.length === 0 && (
              <div className="no-questions">No questions found.</div>
            )}
          </div>
        </aside>

        {/* Question Details Panel */}
        <section className="question-details">
          {selectedQuestion ? (
            <div className="question-content">
              <div className="question-header">
                <h3 className="question-title">Q: {selectedQuestion.id}</h3>
                <p className="question-description">{selectedQuestion.question}</p>
              </div>
              
              <div className="question-meta">
                <div className="meta-item">
                  <strong className="meta-label">Tags:</strong> 
                  <span className="meta-value">{selectedQuestion.tags.join(', ')}</span>
                  {selectedQuestion.difficulty && (
                    <span className={`difficulty-badge difficulty-${selectedQuestion.difficulty.toLowerCase()}`}>
                      {selectedQuestion.difficulty}
                    </span>
                  )}
                </div>
                <div className="meta-item">
                  <strong className="meta-label">Constraints:</strong> 
                  <span className="meta-value">{selectedQuestion.constraints}</span>
                </div>
                <div className="meta-item">
                  <strong className="meta-label">Input Format:</strong> 
                  <span className="meta-value">{selectedQuestion.input_format}</span>
                </div>
                <div className="meta-item">
                  <strong className="meta-label">Output Format:</strong> 
                  <span className="meta-value">{selectedQuestion.output_format}</span>
                </div>
              </div>
              
              <div className="examples-section">
                <div className="example-block">
                  <strong className="example-label">Sample Input:</strong>
                  <pre className="example-code">{selectedQuestion.demo_input}</pre>
                </div>
                <div className="example-block">
                  <strong className="example-label">Sample Output:</strong>
                  <pre className="example-code">{selectedQuestion.demo_output}</pre>
                </div>
              </div>
              
              <div className="action-section">
                <button 
                  className="solve-button" 
                  onClick={() => window.location.href = `/editor?qid=${selectedQuestion.id}`}
                >
                  Go to Editor & Solve
                </button>
              </div>
            </div>
          ) : (
            <div className="empty-state">
              <Code size={48} className="empty-icon" />
              <h3 className="empty-title">Select a Question</h3>
              <p className="empty-description">Choose a question from the left panel to view details and start solving</p>
            </div>
          )}
        </section>
      </main>
    </div>
  );
};

export default Dashboard;
