import React, { useState } from 'react';
import { Routes, Route, Link, useLocation } from 'react-router-dom';
import Dashboard from './components/dashboard/dashboard';
import Profile from './components/profile/Profile';
import Rooms from './components/rooms/Rooms';
import Editor from './components/editor/Editor';
import Login from './components/auth/Login';
import Signup from './components/auth/Signup';
import './App.css';

function App() {
  const location = useLocation();
  const [isLoggedIn, setIsLoggedIn] = useState(false); // This should be managed by your auth system
  const [showUserMenu, setShowUserMenu] = useState(false);
  const [username, setUsername] = useState('JohnDoe'); // This should come from your auth system

  const handleLogout = () => {
    setIsLoggedIn(false);
    setShowUserMenu(false);
    // Add your logout logic here
  };

  return (
    <div className="App">
      {/* Top Header with Auth */}
      <header className="top-header">
        <div className="logo">ZCODER</div>
        <div className="auth-section">
          {isLoggedIn ? (
            <div className="user-menu-container">              <button 
                className="user-avatar"
                onClick={() => setShowUserMenu(!showUserMenu)}
              >
                <span className="avatar-icon">●</span>
                <span className="username">{username}</span>
                <span className="dropdown-arrow">▼</span>
              </button>
              {showUserMenu && (
                <div className="user-dropdown">
                  <Link to="/profile" onClick={() => setShowUserMenu(false)}>Profile</Link>
                  <button onClick={handleLogout}>Sign Out</button>
                </div>
              )}
            </div>
          ) : (
            <div className="auth-buttons">
              <Link to="/login" className="auth-btn login-btn">Login</Link>
              <Link to="/signup" className="auth-btn signup-btn">Sign Up</Link>
            </div>
          )}
        </div>
      </header>

      <div className="app-layout">
        {/* Sidebar Navigation */}
        <aside className="sidebar">
          <nav className="sidebar-nav">            <Link 
              to="/dashboard" 
              className={`sidebar-link ${(location.pathname === '/dashboard' || location.pathname === '/') ? 'active' : ''}`}
            >
              <span className="sidebar-icon">⚡</span>
              <span className="sidebar-text">Dashboard</span>
            </Link>
            <Link 
              to="/rooms" 
              className={`sidebar-link ${location.pathname === '/rooms' ? 'active' : ''}`}
            >
              <span className="sidebar-icon">◉</span>
              <span className="sidebar-text">Rooms</span>
            </Link>
            <Link 
              to="/editor" 
              className={`sidebar-link ${location.pathname === '/editor' ? 'active' : ''}`}
            >
              <span className="sidebar-icon">◈</span>
              <span className="sidebar-text">Editor</span>
            </Link>
            <Link 
              to="/profile" 
              className={`sidebar-link ${location.pathname === '/profile' ? 'active' : ''}`}
            >
              <span className="sidebar-icon">◐</span>
              <span className="sidebar-text">Profile</span>
            </Link></nav>
        </aside>

        {/* Main Content */}
        <main className="main-content">
          <Routes>
            <Route path="/" element={<Dashboard />} />
            <Route path="/dashboard" element={<Dashboard />} />
            <Route path="/profile" element={<Profile />} />
            <Route path="/rooms" element={<Rooms />} />
            <Route path="/editor" element={<Editor />} />
            <Route path="/login" element={<Login />} />
            <Route path="/signup" element={<Signup />} />
          </Routes>
        </main>
      </div>

      <footer className="app-footer">
        <div className="footer-content">
          <div className="footer-logo">© ZCODER</div>
          <div className="footer-links">
            <a href="#">About</a>
            <a href="#">Contact</a>
            <a href="#">Privacy</a>
            <a href="#">Terms</a>
          </div>
        </div>
      </footer>
    </div>
  );
}

export default App;
