import React, { useState, useEffect, useRef } from 'react';
import { fetchQuestions } from '../../utils/questions';
import { Play, Download, Copy, Sun, Moon, Code2, XCircle, User, LogOut } from 'lucide-react';
import './Editor.css';
import axios from "axios";
import { CheckCircle } from "react-feather";
import SplitPane from 'react-split-pane';

const LANGUAGES = [
  { value: 'javascript', label: 'JavaScript', icon: <Code2 size={16} /> },
  { value: 'python', label: 'Python', icon: <Code2 size={16} /> },
  { value: 'java', label: 'Java', icon: <Code2 size={16} /> },
  { value: 'cpp', label: 'C++', icon: <Code2 size={16} /> },
  { value: 'c', label: 'C', icon: <Code2 size={16} /> },
];

const THEMES = [
  { value: 'dark', label: 'Dark', icon: <Moon size={16} /> },
  { value: 'light', label: 'Light', icon: <Sun size={16} /> },
];

const Editor = () => {
  const [code, setCode] = useState('// Start coding...');
  const [language, setLanguage] = useState('javascript');
  const [theme, setTheme] = useState('dark');
  const [fontSize, setFontSize] = useState(16);
  const [input, setInput] = useState('');
  const [questions, setQuestions] = useState([]);
  const [selectedQid, setSelectedQid] = useState('');
  const [selectedQuestion, setSelectedQuestion] = useState(null);
  const [username, setUsername] = useState('');
  const [isCopied, setIsCopied] = useState(false);

  useEffect(() => {
    fetchQuestions().then(qs => {
      setQuestions(qs);
      const qid = new URLSearchParams(window.location.search).get('qid');
      if (qid) {
        setSelectedQid(qid);
        const q = qs.find(q => q._id === qid);
        setSelectedQuestion(q || null);
      }
    });
  }, []);

  useEffect(() => {
    const storedUsername = localStorage.getItem('username');
    if (storedUsername) setUsername(storedUsername);
  }, []);

  const handleCopy = () => {
    navigator.clipboard.writeText(code);
    setIsCopied(true);
    setTimeout(() => setIsCopied(false), 1000);
  };

  const onRunCode = async () => {
    try {
      const res = await axios.post("http://localhost:5000/api/code/execute", {
        code, language, input,
        userId: localStorage.getItem("userId"),
        questionId: selectedQid
      });
      alert("Code executed: check backend response");
    } catch (err) {
      alert("Error running code");
    }
  };

  return (
    <div className={`editor-glass editor-theme-${theme}`}>
      {/* Toolbar */}
      <div className="editor-toolbar">
        <select value={language} onChange={e => setLanguage(e.target.value)}>
          <option value="javascript">JavaScript</option>
          <option value="cpp">C++</option>
          <option value="python">Python</option>
          <option value="java">Java</option>
        </select>

        <select value={theme} onChange={e => setTheme(e.target.value)}>
          <option value="dark">Dark</option>
          <option value="light">Light</option>
        </select>

        <select value={selectedQid} onChange={e => setSelectedQid(e.target.value)}>
          <option value="">Select Question</option>
          {questions.map(q => (
            <option key={q._id} value={q._id}>{q.id} - {q.question.slice(0, 30)}</option>
          ))}
        </select>

        <button onClick={handleCopy}><Copy size={16} /></button>
        <button onClick={onRunCode}><Play size={16} /></button>
        <button onClick={() => {
          localStorage.clear();
          window.location.href = '/login';
        }}><LogOut size={16} /></button>
      </div>

      {/* Split Layout */}
      <SplitPane split="vertical" minSize={300} defaultSize="45%">
        {/* Left: Question Description */}
        <div className="left-question-panel">
          {selectedQuestion ? (
            <div className="question-box">
              <h2><strong>Q{selectedQuestion.id}. {selectedQuestion.question}</strong></h2>
              <h3>Input Format</h3>
              <pre>{selectedQuestion.input_format}</pre>
              <h3>Output Format</h3>
              <pre>{selectedQuestion.output_format}</pre>
              <h3>Sample Input</h3>
              <pre>{selectedQuestion.sample_input}</pre>
              <h3>Sample Output</h3>
              <pre>{selectedQuestion.sample_output}</pre>
            </div>
          ) : (
            <p>Please select a question.</p>
          )}
        </div>

        {/* Right: Code Editor + Input */}
        <div className="right-code-panel">
          <textarea
            className="code-editor"
            style={{ fontSize: fontSize }}
            value={code}
            onChange={e => setCode(e.target.value)}
            spellCheck={false}
          />

          <div className="input-stdin-box">
            <label>Input (stdin)</label>
            <textarea
              value={input}
              onChange={e => setInput(e.target.value)}
              placeholder="Enter sample input"
            />
          </div>
        </div>
      </SplitPane>
    </div>
  );
};

export default Editor;
