import React, { useState, useEffect } from 'react';
import { Code, Star } from 'lucide-react';
import './dashboard.css';
import { fetchQuestions } from '../../utils/questions';

const Dashboard = () => {
  const [selectedQuestion, setSelectedQuestion] = useState(null);
  const [questions, setQuestions] = useState([]);
  const [bookmarkedQuestions, setBookmarkedQuestions] = useState(new Set());
  const [filterTag, setFilterTag] = useState('');
  const [search, setSearch] = useState('');

  useEffect(() => {
    fetchQuestions().then(setQuestions);
  }, []);

  const toggleBookmark = (qid) => {
    const newBookmarks = new Set(bookmarkedQuestions);
    newBookmarks.has(qid) ? newBookmarks.delete(qid) : newBookmarks.add(qid);
    setBookmarkedQuestions(newBookmarks);
  };

  const allTags = Array.from(
    new Set(questions.flatMap(q => q.tags.concat(q.difficulty ? [q.difficulty] : [])))
  );

  const filteredQuestions = questions.filter(q =>
    (!filterTag || q.tags.includes(filterTag) || q.difficulty === filterTag) &&
    (!search || q.question.toLowerCase().includes(search.toLowerCase()))
  );

  const FeatureCard = ({ icon, title, desc }) => (
    <div className="feature-card">
      <h3>{icon} {title}</h3>
      <p>{desc}</p>
    </div>
  );

  const QuestionCard = ({ question }) => (
    <div className={`problem-card ${selectedQuestion?.id === question.id ? 'border-teal-400' : ''}`}
      onClick={() => setSelectedQuestion(question)}>
      <div className="problem-card-header">
        <h3>Q: {question.id}</h3>
        <button onClick={e => { e.stopPropagation(); toggleBookmark(question.id); }}>
          <Star size={16} fill={bookmarkedQuestions.has(question.id) ? '#ecc94b' : 'none'} />
        </button>
      </div>
      <p>{question.question}</p>
      <div className="tags">
        {question.tags.map(tag => <span key={tag} className="tag">{tag}</span>)}
        {question.difficulty && (
          <span className={`tag difficulty-tag difficulty-${question.difficulty.toLowerCase()}`}>
            {question.difficulty}
          </span>
        )}
      </div>
    </div>
  );

  return (
    <div className="dashboard-container">
      {/* HERO SECTION */}
      <div className="hero-section">
        <h1 className="hero-title">ðŸ‘‹ Welcome back, Lakshay!</h1>
        <p className="hero-subtitle">Ready to sharpen your coding skills? Let's get started ðŸ’»</p>
      </div>

      {/* FEATURES */}
      <div className="feature-grid">
        <FeatureCard icon="ðŸ‘¤" title="Personalized Profile" desc="Build your unique coding identity and show off your achievements." />
        <FeatureCard icon="ðŸ¤" title="Collaborative Learning" desc="Share solutions, get feedback, and level up with peers." />
        <FeatureCard icon="âš™ï¸" title="Efficient Practice" desc="Save time with streamlined tools and smart evaluation." />
      </div>

      {/* MAIN SECTION */}
      <div className="dashboard-main">
        {/* LEFT - Question List */}
        <div className="dashboard-left">
          <h3>Questions</h3>
          <div className="filters">
            <input
              type="text"
              placeholder="Search..."
              value={search}
              onChange={e => setSearch(e.target.value)}
            />
            <select value={filterTag} onChange={e => setFilterTag(e.target.value)}>
              <option value="">All Tags</option>
              {allTags.map(tag => <option key={tag} value={tag}>{tag}</option>)}
            </select>
          </div>
          <div className="question-list">
            {filteredQuestions.map(q => <QuestionCard key={q.id} question={q} />)}
            {filteredQuestions.length === 0 && <div className="empty-msg">No questions found.</div>}
          </div>
        </div>

        {/* RIGHT - Question Detail */}
        <div className="dashboard-right">
          {selectedQuestion ? (
            <div className="question-detail">
              <h3>Q: {selectedQuestion.id}</h3>
              <p>{selectedQuestion.question}</p>
              <p><strong>Tags:</strong> {selectedQuestion.tags.join(', ')} {selectedQuestion.difficulty && <span className={`tag difficulty-tag difficulty-${selectedQuestion.difficulty.toLowerCase()}`}>{selectedQuestion.difficulty}</span>}</p>
              <p><strong>Constraints:</strong> {selectedQuestion.constraints}</p>
              <p><strong>Input Format:</strong> {selectedQuestion.input_format}</p>
              <p><strong>Output Format:</strong> {selectedQuestion.output_format}</p>
              <div><strong>Sample Input:</strong><pre>{selectedQuestion.demo_input}</pre></div>
              <div><strong>Sample Output:</strong><pre>{selectedQuestion.demo_output}</pre></div>
              <button onClick={() => window.location.href = `/editor?qid=${selectedQuestion.id}`} className="primary-button">Go to Editor & Solve</button>
            </div>
          ) : (
            <div className="no-question-selected">
              <Code size={48} />
              <h3>Select a Question</h3>
              <p>Choose a question from the left panel to view details and start solving.</p>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
