import React, { useState, useEffect } from 'react';
import { Code, Star } from 'lucide-react';
import './dashboard.css';
import { fetchQuestions } from '../../utils/questions';

const Dashboard = () => {
  const [selectedQuestion, setSelectedQuestion] = useState(null);
  const [questions, setQuestions] = useState([]);
  const [bookmarkedQuestions, setBookmarkedQuestions] = useState(new Set());
  const [filterTag, setFilterTag] = useState('');
  const [search, setSearch] = useState('');

  useEffect(() => {
    fetchQuestions().then(setQuestions);
  }, []);

  const toggleBookmark = (qid) => {
    const newBookmarks = new Set(bookmarkedQuestions);
    newBookmarks.has(qid) ? newBookmarks.delete(qid) : newBookmarks.add(qid);
    setBookmarkedQuestions(newBookmarks);
  };

  const allTags = Array.from(
    new Set(questions.flatMap(q => q.tags.concat(q.difficulty ? [q.difficulty] : [])))
  );

  const filteredQuestions = questions.filter(q =>
    (!filterTag || q.tags.includes(filterTag) || q.difficulty === filterTag) &&
    (!search || q.question.toLowerCase().includes(search.toLowerCase()))
  );

  const totalQuestions = questions.length;
  const solvedCount = 0;
  const bookmarkedCount = bookmarkedQuestions.size;
  const acceptanceRate = '—';

  const QuestionCard = ({ question }) => (
    <div
      className={`problem-card ${selectedQuestion?.id === question.id ? 'border-teal-400' : ''}`}
      onClick={() => setSelectedQuestion(question)}
    >
      <div className="problem-card-header">
        <h3>Q: {question.id}</h3>
        <button onClick={e => { e.stopPropagation(); toggleBookmark(question.id); }}>
          <Star
            size={16}
            fill={bookmarkedQuestions.has(question.id) ? '#ecc94b' : 'none'}
            color={bookmarkedQuestions.has(question.id) ? '#ecc94b' : '#b2f5ea'}
          />
        </button>
      </div>
      <p className="problem-card-title">{question.question}</p>
      <div className="tags">
        {question.tags.map(tag => <span key={tag} className="tag">{tag}</span>)}
        {question.difficulty && (
          <span className={`tag difficulty-tag difficulty-${question.difficulty.toLowerCase()}`}>
            {question.difficulty}
          </span>
        )}
      </div>
    </div>
  );

  return (
  <div className="dashboard-container">
    {/* Header */}
    <h1 style={{ fontSize: '2.8rem', marginTop: '32px', marginBottom: '8px', fontWeight: '800', color: '#38b2ac' }}>
      Dashboard · ZCODER
    </h1>

    <p style={{ fontStyle: 'italic', fontSize: '1.3rem', color: '#b2f5ea', marginBottom: '12px' }}>
      Welcome back, coder! Keep your streak strong and your logic sharper.
    </p>

    <p style={{
      fontSize: '1.1rem',
      color: '#c4f1f9',
      lineHeight: 1.6,
      maxWidth: '960px',
      marginBottom: '16px',
      fontWeight: '500'
    }}>
      Your journey to becoming a code ninja starts here. Dive into handpicked problems, flex your brain,
      and build that muscle memory for patterns. This isn’t just another platform—it’s your personal battleground
      of bugs and brilliance.
    </p>
    <div style={{ display: 'flex', gap: '24px', marginTop: '32px', marginBottom: '12px', flexWrap: 'wrap' }}>
      <div className="stat-card">
        <h4>Total Questions</h4>
        <p>{questions.length}</p>
      </div>
      <div className="stat-card">
        <h4>Solved</h4>
        <p>--</p>
      </div>
      <div className="stat-card">
        <h4>Submissions</h4>
        <p>--</p>
      </div>
      <div className="stat-card">
        <h4>Bookmarked</h4>
        <p>{bookmarkedQuestions.size}</p>
      </div>
    </div>

    {/* Pro Tips (no wrapper) */}
    <h3 style={{ color: '#38b2ac', fontSize: '20px', marginBottom: '12px', marginTop: '10px' }}>Pro Tips 🧠</h3>
    <ul style={{ color: '#e6fffa', fontSize: '16px', lineHeight: 1.8, paddingLeft: '20px', marginBottom: '24px' }}>
      <li><strong>🔥 Filter wisely:</strong> Master one tag at a time. Today trees, tomorrow DP.</li>
      <li><strong>📚 Bookmark the beasts:</strong> Found a tough nut? Mark it, crack it later.</li>
      <li><strong>🛠️ Build patterns:</strong> Repetition isn’t boring—it’s brain gains.</li>
      <li><strong>🧪 Debug like a scientist:</strong> Hypothesize, test, iterate. Your console is your lab.</li>
      <li><strong>⚡ Speed comes later:</strong> First get it right. Then get it fast.</li>
    </ul>

    {/* QUESTIONS SECTION */}
    <div className="dashboard-section" style={{ display: 'flex', gap: 32, flexWrap: 'wrap', marginTop: 32 }}>
      {/* Left: Scrollable Questions List with Filter */}
      <div style={{ flex: 1, minWidth: 320, maxHeight: 600, overflowY: 'auto', borderRight: '1px solid #23272f', paddingRight: 16 }}>
        <h3 style={{ color: '#38b2ac', marginBottom: 12 }}>Questions</h3>
        <div style={{ display: 'flex', gap: 8, marginBottom: 10 }}>
          <input
            type="text"
            placeholder="Search..."
            value={search}
            onChange={e => setSearch(e.target.value)}
            style={{ flex: 1, borderRadius: 6, border: '1px solid #38b2ac', padding: '4px 8px', background: '#23272f', color: '#b2f5ea' }}
          />
          <select
            value={filterTag}
            onChange={e => setFilterTag(e.target.value)}
            style={{ borderRadius: 6, border: '1px solid #38b2ac', background: '#23272f', color: '#b2f5ea', padding: '4px 8px' }}
          >
            <option value="">All Tags</option>
            {allTags.map(tag => (
              <option key={tag} value={tag}>{tag}</option>
            ))}
          </select>
        </div>
        <div style={{ display: 'flex', flexDirection: 'column', gap: 12 }}>
          {filteredQuestions.map(q => (
            <QuestionCard key={q.id} question={q} />
          ))}
          {filteredQuestions.length === 0 && <div style={{ color: '#b2f5ea', textAlign: 'center', marginTop: 24 }}>No questions found.</div>}
        </div>
      </div>

      {/* Right: Question Details and Submit Button */}
      <div style={{ flex: 2, minWidth: 320 }}>
        {selectedQuestion ? (
          <div className="code-editor-container">
            <h3 style={{ color: '#b2f5ea', fontWeight: 600, fontSize: 20, marginTop: 0, marginBottom: 16 }}>Q: {selectedQuestion.id}</h3>
            <div style={{ color: '#b2f5ea', marginBottom: 16, fontSize: 16 }}>{selectedQuestion.question}</div>
            <div style={{ marginBottom: 10 }}>
              <strong style={{ color: '#38b2ac' }}>Tags:</strong> {selectedQuestion.tags.join(', ')}
              {selectedQuestion.difficulty && (
                <span className={`tag difficulty-tag difficulty-${selectedQuestion.difficulty.toLowerCase()}`} style={{ marginLeft: 8 }}>{selectedQuestion.difficulty}</span>
              )}
            </div>
            <div style={{ marginBottom: 10 }}>
              <strong style={{ color: '#38b2ac' }}>Constraints:</strong> <span style={{ color: '#b2f5ea' }}>{selectedQuestion.constraints}</span>
            </div>
            <div style={{ marginBottom: 10 }}>
              <strong style={{ color: '#38b2ac' }}>Input Format:</strong> <span style={{ color: '#b2f5ea' }}>{selectedQuestion.input_format}</span>
            </div>
            <div style={{ marginBottom: 10 }}>
              <strong style={{ color: '#38b2ac' }}>Output Format:</strong> <span style={{ color: '#b2f5ea' }}>{selectedQuestion.output_format}</span>
            </div>
            <div style={{ marginBottom: 10 }}>
              <strong style={{ color: '#38b2ac' }}>Sample Input:</strong>
              <pre style={{ background: '#23272f', color: '#b2f5ea', borderRadius: 6, padding: 8, margin: 0 }}>{selectedQuestion.demo_input}</pre>
            </div>
            <div style={{ marginBottom: 18 }}>
              <strong style={{ color: '#38b2ac' }}>Sample Output:</strong>
              <pre style={{ background: '#23272f', color: '#b2f5ea', borderRadius: 6, padding: 8, margin: 0 }}>{selectedQuestion.demo_output}</pre>
            </div>
            <button
              className="action-button primary-button"
              style={{ marginTop: 16 }}
              onClick={() => window.location.href = `/editor?qid=${selectedQuestion.id}`}
            >
              Go to Editor & Solve
            </button>
          </div>
        ) : (
          <div style={{ color: '#b2f5ea', textAlign: 'center', padding: 32 }}>
            <Code size={48} style={{ color: '#23272f', marginBottom: 12 }} />
            <h3 style={{ fontWeight: 600, fontSize: 18 }}>Select a Question</h3>
            <p>Choose a question from the left panel to view details and start solving</p>
          </div>
        )}
      </div>
    </div>
  </div>
);

};

export default Dashboard;
